// SPDX-License-Identifier: GPL-3.0

pragma solidity >=0.8.2 <0.9.0;

enum MPStatus {OPEN, CLOSED, FROZEN, DISSOLVED}

struct ProtoPension { 
    address owner; 
    uint256 productId; 
}

struct MPension { 
    uint256 id; 
    address creator; 
    address owner; 
    address treasury; 
    address vault; 
    uint256 maturityDate; 
    uint256 dissolutionDate; 
    uint256 contributionCount; 
    MPStatus status; 
    MPProduct product; 
}

struct MPProduct { 
    uint256 id; 
    address provider; 
    address pensionCurrencyErc20; 
    RiskType riskType; 
    TreasuryType treasuryType; 
    FinancialSchedule paymentSchedule; 
    FinancialSchedule emissionSchedule; 
}

struct MProtoProduct{ 
    address pensionCurrencyErc20; 
    RiskType riskType; 
    TreasuryType treasuryType; 
    FinancialSchedule paymentSchedule; 
    FinancialSchedule emissionSchedule;
    Tolerance tolerance; 
}

enum TreasuryType{FIXED, DYNAMIC}

enum RiskType {HIGH, MEDIUM, LOW}

enum Schedule {DAILY, WEEKLY, MONTHLY, QUARTERLY}

struct FinancialSchedule { 
    Schedule schedule; 
    uint256 term; 
    uint256 minimumAmount; 
    uint256 maximumAmount;
    uint256 totalIntervals; 
    uint256 totalAmount; 
}

struct Tolerance { 
    uint256 maxGainPercentage; 
    uint256 maxLossPercentage; 
}


enum TxType {DEPOSIT, EMISSION, WITHDRAW, DISSOLVE, PULL, PUSh, INVEST, DIVEST}

struct Tx { 
    uint256 id; 
    TxType txType; 
    uint256 mPensionId; 
    uint256 date; 
    uint256 amount; 
    address erc20; 
}

enum PStatus {OPEN, CLOSED}

struct Position { 
    uint256 id; 
    PStatus status; 
    uint256 mPensionId; 
    uint256 amount; 
    uint256 created; 
    Tolerance tolerance; 
    address market; 
}
