// SPDX-License-Identifier: GPL-3.0

pragma solidity >=0.8.2 <0.9.0;

enum MPStatus {OPEN, DISBURSEMENT, CLOSED, FROZEN}
enum MPScheduleStatus {MATURED/**no further payments remaining*/, SETTLED /** no further emissions remaining */}

struct ProtoPension { 
    address owner; 
    uint256 productId; 
}

struct MPension { 
    uint256 id; 
    address creator; 
    address owner; 
    address treasury; 
    address vault; 
    uint256 maturityDate; 
    uint256 dissolutionDate; 
    MPProduct product; 
}

struct MPProduct { 
    uint256 id; 
    address provider; 
    address erc20; 
    uint256 principalOnMaturity; 
    uint256 interest; 
    RiskType riskType; 
    address treasury;
    TreasuryType treasuryType; 
    FinancialSchedule contributionSchedule; 
    FinancialSchedule emissionSchedule; 
    Tolerance tolerance; 
}

struct MProtoProduct{ 
    address pensionCurrencyErc20; 
    RiskType riskType; 
    TreasuryType treasuryType; 
    uint256 totalContribution; 
    uint256 principalOnMaturity; 
    uint256 interest; 
    FinancialSchedule contributionSchedule; 
    FinancialSchedule emissionSchedule;
    Tolerance tolerance; 
}

enum TreasuryType{FIXED, DYNAMIC}

struct TreasuryBond { 
    uint256 id; 
    address owner; 
    uint256 amount; 
    address erc20; 
}

struct MarketBond { 
    uint256 id; 
    address owner; 
    address fromToken;
    address toToken;
    uint256 minDuration; 
    uint256 maxDuration; 
    uint256 minReturn; 
    uint256 maxSlots; 
    uint256 marketBondAmount; 
    address marketBondErc20;
    uint256 createDate; 
    uint256 expiryDate; 
    uint256 finalExitDate; 
}

enum RiskType {HIGH, MEDIUM, LOW}

enum Schedule {DAILY, WEEKLY, MONTHLY, QUARTERLY}

struct FinancialSchedule { 
    Schedule schedule; 
    uint256 term; 
    uint256 minimumAmount; 
    uint256 maximumAmount;
    uint256 totalIntervals; 
    uint256 totalAmount; 
}

struct Tolerance { 
    uint256 maxGainPercentage; 
    uint256 maxLossPercentage; 
}


enum TxType {CREATE, CONTRIBUTE, DRAWDOWN, PULL, PUSh, INVEST, DIVEST, CLOSE}

struct Tx { 
    uint256 id; 
    TxType txType; 
    address location; 
    uint256 mPensionId; 
    uint256 date; 
    uint256 amount; 
    address erc20; 
}

enum PStatus {OPEN, CLOSED}

struct Position { 
    uint256 id; 
    PStatus status; 
    uint256 mPensionId; 
    uint256 amount; 
    uint256 created; 
    Tolerance tolerance; 
    address market; 
}
